import React, { useRef, useEffect, useState } from "react";
import "./App.css";
import Webcam from "react-webcam";

function App() {
  const webcamRef = useRef(null);
  const [finalEmotion, setFinalEmotion] = useState("Neutral");
  const [finalPercentage, setFinalPercentage] = useState(0);
  const [currentPage, setCurrentPage] = useState("home"); // Manage the current section
  const [selectedImage, setSelectedImage] = useState(null);

  // Function to handle emotion detection from the webcam
  const detect = async () => {
    if (
      currentPage === "face-detection" &&
      webcamRef.current &&
      webcamRef.current.video.readyState === 4
    ) {
      const imageSrc = webcamRef.current.getScreenshot();
      sendImageToServer(imageSrc);
    }
  };

  // Handle image upload
  const handleImageUpload = async (event) => {
    const file = event.target.files[0];
    const reader = new FileReader();

    reader.onloadend = () => {
      const base64Image = reader.result;
      sendImageToServer(base64Image);
      setSelectedImage(base64Image); // Display the uploaded image
    };

    if (file) {
      reader.readAsDataURL(file); // Convert image file to base64 string
    }
  };

  // Function to send image to the server for emotion detection
  const sendImageToServer = (imageSrc) => {
    const socket = new WebSocket("ws://localhost:8000");
    const apiCall = {
      event: "localhost:subscribe",
      data: { image: imageSrc },
    };

    socket.onopen = () => {
      console.log("WebSocket connection opened. Sending image data...");
      socket.send(JSON.stringify(apiCall));
    };

    socket.onmessage = (event) => {
      console.log("Message received from server:", event.data);
      const pred_log = JSON.parse(event.data);
      if (!pred_log.error) {
        const predictions = pred_log["predictions"];
        const emotions = Object.keys(predictions);
        const values = emotions.map((emotion) => predictions[emotion] * 100);

        const maxIndex = values.indexOf(Math.max(...values));
        const emotion = emotions[maxIndex];
        const percentage = Math.round(values[maxIndex]);

        setFinalEmotion(emotion);
        setFinalPercentage(percentage);
      } else {
        console.error("Error in prediction:", pred_log.error);
      }
    };

    socket.onerror = (error) => {
      console.error("WebSocket error:", error);
    };

    socket.onclose = () => {
      console.log("WebSocket connection closed.");
    };
  };

  // Detect emotions from the webcam every second
  useEffect(() => {
    const interval = setInterval(detect, 1000);
    return () => clearInterval(interval);
  }, [currentPage]);

  // Handle navigation clicks
  const handleNavClick = (page) => {
    setCurrentPage(page);
    if (page === "input-image") {
      setFinalEmotion("None"); // Reset emotion to "None"
      setFinalPercentage(0);    // Reset percentage to 0
      setSelectedImage(null);   // Clear previously selected image
    }
  };

  return (
    <div className="App">
      {/* Navbar */}
      <nav>
        <button onClick={() => handleNavClick("home")}>Home</button>
        <button onClick={() => handleNavClick("face-detection")}>Face Detection</button>
        <button onClick={() => handleNavClick("input-image")}>Input Image</button>
      </nav>

      {/* Home */}
      {currentPage === "home" && <div>Welcome to the Emotion Detection App</div>}

      {/* Face Detection */}
      {currentPage === "face-detection" && (
        <>
          <Webcam
            ref={webcamRef}
            style={{
              position: "absolute",
              marginLeft: "auto",
              marginRight: "auto",
              left: 0,
              right: 600,
              top: 20,
              textAlign: "center",
              zIndex: 9,
              width: 640,
              height: 480,
            }}
          />

          <div className="Prediction" style={{ position: "absolute", right: 100, width: 500, top: 60 }}>
            <label htmlFor="finalEmotionProgress" style={{ color: "black" }}>
              Emotion : {finalEmotion} ({finalPercentage}%)
            </label>
            <progress id="finalEmotionProgress" value={finalPercentage} max="100">
              {finalPercentage}%
            </progress>
            <br /><br />

            <input
              id="emotion_text"
              name="emotion_text"
              value={finalEmotion}
              style={{
                width: 200,
                height: 50,
                fontSize: "30px",
                textAlign: "center",
              }}
              readOnly
            />
          </div>
        </>
      )}

      {/* Input Image */}
      {currentPage === "input-image" && (
        <div style={{ textAlign: "center", marginTop: 50 }}>
          <input type="file" onChange={handleImageUpload} />
          {selectedImage && (
            <>
              <img src={selectedImage} alt="Uploaded" style={{ width: 300, marginTop: 20 }} />
              
              <div className="Prediction" style={{ marginTop: 20 }}>
                <label htmlFor="finalEmotionProgress" style={{ color: "black" }}>
                  Emotion : {finalEmotion} ({finalPercentage}%)
                </label>
                <progress id="finalEmotionProgress" value={finalPercentage} max="100">
                  {finalPercentage}%
                </progress>
                <br /><br />

                <input
                  id="emotion_text"
                  name="emotion_text"
                  value={finalEmotion}
                  style={{
                    width: 200,
                    height: 50,
                    fontSize: "30px",
                    textAlign: "center",
                  }}
                  readOnly
                />
              </div>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;

